$(document).ready(function(){$("#submit_button").on("click",function(event){event.preventDefault();var feeling=$('#feeling').find('input[name="feeling"]:checked').val();var my_lat=$('#my_lat').val();var my_long=$('#my_long').val();var note=$('#textarea1').val();var jsonString={"feeling":feeling,"my_lat":my_lat,"my_long":my_long,"note":note}
$.ajax({type:'post',url:'check_in.php',data:jsonString,beforeSend:function(){},success:function(data){if(data){$('#check_in_message').show();$('#check_in_message').html(data);$('#check_in_message').delay(3000).fadeOut(1500);$('#checkInForm')[0].reset();}else{alert("oops nothing happened :(");}}});});function get_history(){$.ajax({type:'post',url:'get_hist.php',dataType:'json',success:function(data){if(data){$('.row_hist').remove();for(var i=0;i<data.length;i++){$('#tbody_hist').after('<tr class="row_hist"><td class="lat_hist">'+data[i].lat+'</td><td class="long_hist">'+data[i].my_long+'</td><td class="feeling_hist">'+data[i].feeling+'</td><td>'+data[i].note+'</td></tr>');}}else{alert("oops nothing happened :(");}}});}
$('#update_hist_button').click(function(event){event.preventDefault();get_history();});});
